 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void counting_sort()
&amp;#123;
    int sorted[N];
    int maxv = a[0];
    for (int i = 1; i &amp;lt; n; i ++ )
        if (maxv &amp;lt; a[i])
            maxv = a[i];
    int count[maxv+1];
    for (int i = 0; i &amp;lt; n; i ++ ) count[a[i]] ++ ;
    for (int i = 1; i &amp;lt;= maxv; i ++ ) count[i] += count[i-1];
    for (int i = n-1; i &amp;gt;= 0; i -- )
    &amp;#123;
        sorted[count[a[i]]-1] = a[i];
        count[a[i]] -- ;
    &amp;#125;
    for (int i = 0; i &amp;lt; n; i ++ ) a[i] = sorted[i];
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h6 id=&#34;Âü∫Êï∞ÊéíÂ∫èÊòØÊ°∂ÊéíÂ∫èÁöÑÁâπ‰æã‰ºòÂäøÊòØÂèØ‰ª•Â§ÑÁêÜÊµÆÁÇπÊï∞ÂíåË¥üÊï∞Âä£ÂäøÊòØËøòË¶ÅÈÖçÂêàÂà´ÁöÑÊéíÂ∫èÂáΩÊï∞&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#Âü∫Êï∞ÊéíÂ∫èÊòØÊ°∂ÊéíÂ∫èÁöÑÁâπ‰æã‰ºòÂäøÊòØÂèØ‰ª•Â§ÑÁêÜÊµÆÁÇπÊï∞ÂíåË¥üÊï∞Âä£ÂäøÊòØËøòË¶ÅÈÖçÂêàÂà´ÁöÑÊéíÂ∫èÂáΩÊï∞&#34;&gt;#&lt;/a&gt; ÔºàÂü∫Êï∞ÊéíÂ∫èÊòØÊ°∂ÊéíÂ∫èÁöÑÁâπ‰æãÔºå‰ºòÂäøÊòØÂèØ‰ª•Â§ÑÁêÜÊµÆÁÇπÊï∞ÂíåË¥üÊï∞ÔºåÂä£ÂäøÊòØËøòË¶ÅÈÖçÂêàÂà´ÁöÑÊéíÂ∫èÂáΩÊï∞Ôºâ&lt;/h6&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;vector&amp;lt;int&amp;gt; bucketSort(vector&amp;lt;int&amp;gt;&amp;amp; nums) &amp;#123;
    int n = nums.size();
    int maxv = *max_element(nums.begin(), nums.end());
    int minv = *min_element(nums.begin(), nums.end());
    int bs = 1000;
    int m = (maxv-minv)/bs+1;
    vector&amp;lt;vector&amp;lt;int&amp;gt; &amp;gt; bucket(m);
    for (int i = 0; i &amp;lt; n; ++i) &amp;#123;
        bucket[(nums[i]-minv)/bs].push_back(nums[i]);
    &amp;#125;
    int idx = 0;
    for (int i = 0; i &amp;lt; m; ++i) &amp;#123;
        int sz = bucket[i].size();
        bucket[i] = quickSort(bucket[i]);
        for (int j = 0; j &amp;lt; sz; ++j) &amp;#123;
            nums[idx++] = bucket[i][j];
        &amp;#125;
    &amp;#125;
    return nums;
&amp;#125;

&lt;/code&gt;&lt;/pre&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void insert_sort()
&amp;#123;
    for (int i = 1; i &amp;lt; n; i ++ )
    &amp;#123;
        int x = a[i];
        int j = i-1;

        while (j &amp;gt;= 0 &amp;amp;&amp;amp; x &amp;lt; a[j])
        &amp;#123;
            a[j+1] = a[j];
            j -- ;
        &amp;#125;
        a[j+1] = x;
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void quick_sort(int l, int r)
&amp;#123;
    if (l &amp;gt;= r) return ;

    int x = a[l+r&amp;gt;&amp;gt;1], i = l-1, j = r+1;
    while (i &amp;lt; j)
    &amp;#123;
        while (a[++ i] &amp;lt; x);
        while (a[-- j] &amp;gt; x);
        if (i &amp;lt; j) swap(a[i], a[j]);
    &amp;#125;
    sort(l, j), sort(j+1, r);
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void merge_sort(int l, int r)
&amp;#123;
    if (l &amp;gt;= r) return;
    int temp[N];
    int mid = l+r&amp;gt;&amp;gt;1;
    merge_sort(l, mid), merge_sort(mid+1, r);
    int k = 0, i = l, j = mid+1;
    while (i &amp;lt;= mid &amp;amp;&amp;amp; j &amp;lt;= r)
    &amp;#123;
        if (a[i] &amp;lt; a[j]) temp[k ++ ] = a[i ++ ];
        else temp[k ++ ] = a[j ++ ];

    &amp;#125;
    while (i &amp;lt;= mid) temp[k ++ ] = a[i ++ ];
    while (j &amp;lt;= r) temp[k ++ ] = a[j ++ ];
    for (int i = l, j = 0; i &amp;lt;= r; i ++ , j ++ ) a[i] = temp[j];
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void shell_sort()
&amp;#123;
    for (int gap = n &amp;gt;&amp;gt; 1; gap; gap &amp;gt;&amp;gt;= 1)
    &amp;#123;
        for (int i = gap; i &amp;lt; n; i ++ )
        &amp;#123;
            int x = a[i];
            int j;
            for (j = i; j &amp;gt;= gap &amp;amp;&amp;amp; a[j-gap] &amp;gt; x; j -= gap)
                a[j] = a[j-gap];
            a[j] = x;
        &amp;#125;
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;p&gt;ÔºàÈ°ªÁü•Ê≠§ÊéíÂ∫è‰∏∫‰ΩøÁî®‰∫ÜÊ®°ÊãüÂ†ÜÔºå‰∏∫‰∫Ü‰ΩøÊúÄÂêé‰∏Ä‰∏™ÈùûÂè∂Â≠êËäÇÁÇπÁöÑÁºñÂè∑‰∏∫ n/2ÔºåÊï∞ÁªÑÁºñÂè∑‰ªé 1 ÂºÄÂßã)&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void down(int u)
&amp;#123;
    int t = u;
    if (u&amp;lt;&amp;lt;1 &amp;lt;= n &amp;amp;&amp;amp; h[u&amp;lt;&amp;lt;1] &amp;lt; h[t]) t = u&amp;lt;&amp;lt;1;
    if ((u&amp;lt;&amp;lt;1|1) &amp;lt;= n &amp;amp;&amp;amp; h[u&amp;lt;&amp;lt;1|1] &amp;lt; h[t]) t = u&amp;lt;&amp;lt;1|1;
    if (u != t)
    &amp;#123;
        swap(h[u], h[t]);
        down(t);
    &amp;#125;
&amp;#125;

int main()
&amp;#123;
    for (int i = 1; i &amp;lt;= n; i ++ ) cin &amp;gt;&amp;gt; h[i];
    for (int i = n/2; i; i -- ) down(i);
    while (true)
    &amp;#123;
        if (!n) break;
        cout &amp;lt;&amp;lt; h[1] &amp;lt;&amp;lt; &#39; &#39;;
        h[1] = h[n];
        n -- ;
        down(1);
    &amp;#125;
    return 0;
&amp;#125;
