 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;int maxbit()
&amp;#123;
    int maxv = a[0];
    for (int i = 1; i &amp;lt; n; i ++ )
        if (maxv &amp;lt; a[i])
            maxv = a[i];
    int cnt = 1;
    while (maxv &amp;gt;= 10) maxv /= 10, cnt ++ ;

    return cnt;
&amp;#125;
void radixsort()
&amp;#123;
    int t = maxbit();
    int radix = 1;

    for (int i = 1; i &amp;lt;= t; i ++ )
    &amp;#123;
        for (int j = 0; j &amp;lt; 10; j ++ ) count[j] = 0;
        for (int j = 0; j &amp;lt; n; j ++ )
        &amp;#123;
            int k = (a[j] / radix) % 10;
            count[k] ++ ;
        &amp;#125;
        for (int j = 1; j &amp;lt; 10; j ++ ) count[j] += count[j-1];
        for (int j = n-1; j &amp;gt;= 0; j -- )
        &amp;#123;
            int k = (a[j] / radix) % 10;
            temp[count[k]-1] = a[j];
            count[k] -- ;
        &amp;#125;
        for (int j = 0; j &amp;lt; n; j ++ ) a[j] = temp[j];
        radix *= 10;
    &amp;#125;

&amp;#125;
