 &lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;void bubble_sort()
&amp;#123;
    for (int i = n-1; i &amp;gt;= 1; i -- )
    &amp;#123;
        bool flag = true;
        for (int j = 1; j &amp;lt;= i; j ++ )
            if (a[j-1] &amp;gt; a[j])
            &amp;#123;
                swap(a[j-1], a[j]);
                flag = false;
            &amp;#125;
        if (flag) return;
    &amp;#125;
&amp;#125;

