 &lt;h3 id=&#34;Ëµ∞Ëø∑ÂÆ´-bfs&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#Ëµ∞Ëø∑ÂÆ´-bfs&#34;&gt;#&lt;/a&gt; &lt;a href=&#34;https://www.acwing.com/problem/content/846/&#34;&gt;Ëµ∞Ëø∑ÂÆ´ - BFS&lt;/a&gt;&lt;/h3&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;iostream&amp;gt;
#include &amp;lt;algorithm&amp;gt;
#include &amp;lt;cstring&amp;gt;

using namespace std;

typedef pair&amp;lt;int, int&amp;gt; PII;

const int N = 110;

int n, m;
int g[N][N];
int d[N][N];
PII q[N * N];

int bfs()&amp;#123;
	int hh = 0, tt = 0;
	q[0] = &amp;#123;0, 0&amp;#125;;
	memset(d, -1, sizeof d);
	d[0][0] = 0;
	int dx[4] = &amp;#123;-1, 0, 1, 0&amp;#125;, dy[4] = &amp;#123;0, 1, 0, -1&amp;#125;;
	while(hh &amp;lt;= tt)&amp;#123;
	//while ÈòüÂàó‰∏çÁ©∫
		auto t = q[hh++];
		//ÊØè‰∏ÄÊ¨°ÂèñÂá∫Êù•ÈòüÂ§¥ÂÖÉÁ¥† Âç≥ t = ÈòüÂ§¥
		for(int i = 0; i &amp;lt; 4; i++)&amp;#123;
			int x = t.first + dx[i], y = t.second + dy[i];
			//Ê≤øÁùÄËøô‰∏™ÊñπÂêëËµ∞
			if(x &amp;gt;= 0 &amp;amp;&amp;amp; x &amp;lt; n &amp;amp;&amp;amp; y &amp;gt;= 0 &amp;amp;&amp;amp; y &amp;lt; m &amp;amp;&amp;amp; g[x][y] == 0 &amp;amp;&amp;amp; d[x][y] == -1)&amp;#123;
				//Âπ∂‰∏îÂú®ËæπÁïåÂÜÖÁöÑËØù                      Âπ∂‰∏îÊòØÁ©∫Âú∞ÂèØ‰ª•Ëµ∞   Âπ∂‰∏îÊ≤°ÊúâËµ∞ËøáÁöÑËØù
				//Â¶ÇÊûú‰∏çÊòØÁ¨¨‰∏ÄÊ¨°ÊêúÂà∞Âç≥Ëµ∞Ëøá‰∏ÄÊ¨°‰∫ÜÁöÑËØùÂ∞±‰∏çÊòØBFS ‰πüÂ∞±‰∏çÊòØÊúÄÁü≠Ë∑ùÁ¶ª
				//Ê≥®ÊÑèËøôÈáåÊòØ‰∏ÄÂúà‰∏ÄÂúàÊêúÁöÑ
				d[x][y] = d[t.first][t.second] + 1;
				q[++ tt] = &amp;#123;x, y&amp;#125;;
				//Â∞ÜËøô‰∏™ÁÇπÂä†ÂÖ•ÈòüÂàó
			&amp;#125;
		&amp;#125;
	&amp;#125;
	return d[n - 1][m - 1];
	//ËæìÂá∫Âè≥‰∏ãËßíËøô‰∏™ÁÇπÁöÑË∑ùÁ¶ª
&amp;#125;

int main()&amp;#123;
	cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
	for(int i = 0; i &amp;lt; n; i++)
		for(int j = 0; j &amp;lt; m; j++)
			cin &amp;gt;&amp;gt; g[i][j];
	cout &amp;lt;&amp;lt; bfs() &amp;lt;&amp;lt; endl;
	return 0;
&amp;#125;
