&lt;h2 id=&#34;Âè™ÊúâÂΩìÊâÄÊúâËæπÁöÑÊùÉÈáçÂç≥ËæπÊùÉÈÉΩÊòØ1ÁöÑÊó∂ÂÄôÊâçÂèØ‰ª•Áî®bfsÊ±ÇÊúÄÁü≠Ë∑Ø‰∏ÄËà¨ÊÉÖÂÜµ‰∏ãÈÉΩË¶ÅÁî®‰∏ìÈó®ÁöÑÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÊ±ÇÊúÄÁü≠Ë∑Ø&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#Âè™ÊúâÂΩìÊâÄÊúâËæπÁöÑÊùÉÈáçÂç≥ËæπÊùÉÈÉΩÊòØ1ÁöÑÊó∂ÂÄôÊâçÂèØ‰ª•Áî®bfsÊ±ÇÊúÄÁü≠Ë∑Ø‰∏ÄËà¨ÊÉÖÂÜµ‰∏ãÈÉΩË¶ÅÁî®‰∏ìÈó®ÁöÑÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÊ±ÇÊúÄÁü≠Ë∑Ø&#34;&gt;#&lt;/a&gt; Âè™ÊúâÂΩìÊâÄÊúâËæπÁöÑÊùÉÈáçÔºàÂç≥ËæπÊùÉÔºâÈÉΩÊòØ 1 ÁöÑÊó∂ÂÄôÊâçÂèØ‰ª•Áî® BFS Ê±ÇÊúÄÁü≠Ë∑Ø‰∏ÄËà¨ÊÉÖÂÜµ‰∏ãÈÉΩË¶ÅÁî®‰∏ìÈó®ÁöÑÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÊ±ÇÊúÄÁü≠Ë∑Ø&lt;/h2&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;//dpÈóÆÈ¢òÂèØ‰ª•Ë¢´ÁúãÊàêÊòØ‰∏ÄÁßçÁâπÊÆäÁöÑÊúÄÁü≠Ë∑ØÈóÆÈ¢ò Âç≥ÊúÄÁü≠Ë∑ØÈóÆÈ¢òÊòØÂåÖÂê´dpÈóÆÈ¢òÁöÑ Âç≥dpÈóÆÈ¢òÂ∞±ÊòØÊ≤°ÊúâÁéØÁöÑÊúÄÁü≠Ë∑Ø
//Ê∑±ÊêúÂèØ‰ª•‰øùËØÅÂèØ‰ª•ÊêúÂà∞ÁªàÁÇπ ‰ΩÜÊòØ‰∏çËÉΩ‰øùËØÅÊêúÂà∞ÁöÑË∑ØÂæÑÊòØÊúÄÁü≠ÁöÑ

//‰∏çÊòØÊâÄÊúâÁöÑÊúÄÁü≠Ë∑ØÈóÆÈ¢òÈÉΩÂèØ‰ª•Áî®bfsÊù•ÂÅö Âè™ÊúâÂΩìÊâÄÊúâËæπÁöÑÊùÉÈáçÈÉΩ‰∏ÄÊ†∑ÁöÑÊó∂ÂÄô ÊØîÂ¶ÇËØ¥ËæπÊùÉ=1Êó∂ ÂèØ‰ª•Áî®bfs
//‰∏ÄËà¨ÊÉÖÂÜµ‰∏ãÈÉΩË¶ÅÁî®‰∏ìÈó®ÁöÑÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÊ±ÇÊúÄÁü≠Ë∑Ø

//dpÈóÆÈ¢òËÇØÂÆö‰∏ç‰ºöÁî®ÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÊù•Ê±Ç Âõ†‰∏∫ÊúÄÁü≠Ë∑ØÁÆóÊ≥ïÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶ÊØîËæÉÈ´ò dpÈóÆÈ¢òÁöÑÊó∂Èó¥Â§çÊùÇÂ∫¶ÊØîËæÉ‰Ωé
#include &amp;lt;cstring&amp;gt;
#include &amp;lt;iostream&amp;gt;
#include &amp;lt;algorithm&amp;gt;
#include &amp;lt;queue&amp;gt;


using namespace std;

const int N = 110;


typedef pair&amp;lt;int, int&amp;gt; PII;

int n, m;
int g[N][N];
int d[N][N];
//Â¶ÇÊûúÈúÄË¶ÅËÆ∞ÂΩïË∑ØÂæÑ Âè™ÈúÄË¶ÅËÆ∞ÂΩï‰∏Ä‰∏ãÂì™ÂÑø‰∏™ÁÇπÊòØ‰ªéÂì™ÂÑø‰∏™ÁÇπÊãìÂ±ïÂá∫Êù•ÁöÑÂ∞±ÂèØ‰ª•‰∫Ü
PII q[N * N], Prev[N][N];

int bfs()&amp;#123;
    int hh = 0, tt = 0;
    q[0] = &amp;#123;0, 0&amp;#125;;

    memset(d, -1, sizeof d);//Â∞ÜÊâÄÊúâË∑ùÁ¶ªÂàùÂßãÂåñÊàê-1 Ë°®Á§∫Ê≤°ÊúâËµ∞Ëøá
    d[0][0] = 0;//dÁ¨¨0ÂàùÂßãÂåñÊàê0 Ë°®Á§∫Â∑≤ÁªèËµ∞Ëøá‰∫Ü

    //ÂèØ‰ª•Áî®ÂêëÈáèË°®Á§∫ÂæÄ‰∏ä‰∏ãÂ∑¶Âè≥Ëµ∞
    //‰∏ä (-1, 0) Âè≥ (0, 1) ‰∏ã (1, 0) Â∑¶ (0, -1)

    //Âç≥  -1         0         1         0
    //        0         1         0         -1
    int dx[4] = &amp;#123;-1, 0, 1, 0&amp;#125;, dy[4] = &amp;#123;0, 1, 0, -1&amp;#125;;

    while(hh &amp;lt;= tt)&amp;#123;
        auto t = q[hh++];//ÊØèÊ¨°ÂèñÂá∫ÈòüÂ§¥

        for(int i = 0; i &amp;lt; 4; i++)&amp;#123;
            int x = t.first + dx[i], y = t.second + dy[i];
            if(x &amp;gt;= 0 &amp;amp;&amp;amp; x &amp;lt; n &amp;amp;&amp;amp; y &amp;gt;= 0 &amp;amp;&amp;amp; y &amp;lt; m &amp;amp;&amp;amp; g[x][y] == 0 &amp;amp;&amp;amp; d[x][y] == -1)&amp;#123;
            //Ëøô‰∏™Âà§Êñ≠ÁöÑÊÑèÊÄùÊòØ Â¶ÇÊûúÊ≤øÁùÄËøô‰∏™ËæπÁïåËµ∞ÁöÑËØùÊòØÂú®ËæπÁïå‰ª•ÂÜÖÁöÑ Âπ∂‰∏îËøô‰∏™ÁÇπÊòØÂèØ‰ª•Ëµ∞ÁöÑ Âç≥ != 0 Âπ∂‰∏îËøô‰∏™ÁÇπÊ≤°ÊúâËµ∞Ëøá Âç≥ d[x][y] == -1
                d[x][y] = d[t.first][t.second] + 1;//BFSÂè™ÊúâÁ¨¨‰∏ÄÊ¨°ÊêúÂà∞ÁöÑÊâçÊòØÊúÄÁü≠Ë∑ùÁ¶ª Â¶ÇÊûú‰∏çÊòØÁ¨¨‰∏ÄÊ¨°ÊêúÂà∞ÈÇ£Â∞±‰∏çÊòØÊúÄÁü≠Ë∑ùÁ¶ª
                Prev[x][y] = t;
                q[++ tt] = &amp;#123;x, y&amp;#125;;
                //Êñ∞ÁöÑ x Êñ∞ÁöÑ y Â°ûËøõÈòüÂàó
            &amp;#125;
        &amp;#125;
    &amp;#125;

    //ËæìÂá∫Ë∑ØÂæÑ
    int x = n - 1, y = m - 1;
    while(x || y)&amp;#123;
        // cout &amp;lt;&amp;lt; x &amp;lt;&amp;lt; &#39; &#39; &amp;lt;&amp;lt; y &amp;lt;&amp;lt; endl;
        auto t = Prev[x][y];
        x = t.first, y = t.second;
    &amp;#125;

    return d[n - 1][m - 1];
&amp;#125;


int main()&amp;#123;
    cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
    for(int i = 0; i &amp;lt; n; i++)
        for(int j = 0; j &amp;lt; m; j++)
            cin &amp;gt;&amp;gt; g[i][j];

    cout &amp;lt;&amp;lt; bfs() &amp;lt;&amp;lt; endl;

    return 0;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;

 &lt;h3 id=&#34;nÁöáÂêéÈóÆÈ¢ò-dfs&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#nÁöáÂêéÈóÆÈ¢ò-dfs&#34;&gt;#&lt;/a&gt; &lt;a href=&#34;https://www.acwing.com/problem/content/845/&#34;&gt;N ÁöáÂêéÈóÆÈ¢ò - DFS&lt;/a&gt;&lt;/h3&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;iostream&amp;gt;

using namespace std;

const int N = 20;

int n;
char g[N][N];
bool col[N], dg[N], udg[N];

void dfs(int u)&amp;#123;
	if(u == n)&amp;#123;
		for(int i = 0; i &amp;lt; n; i++) puts(g[i]);
		puts(&amp;quot;&amp;quot;);
		return;
	&amp;#125;
	for(int i = 0; i &amp;lt; n; i++)&amp;#123;
		if(!col[i] &amp;amp;&amp;amp; !dg[u + i] &amp;amp;&amp;amp; !udg[n - u + i])&amp;#123;
		//Ëøô‰∏™ ÂÅèÁßªÈáè Áõ∏ÂΩì‰∫é Êää Ë¥üÊï∞Âíå Ëøô‰∏™Êï∞ ÁÆÄÂçïÁöÑÊò†Â∞Ñ‰∫Ü ‰∏Ä‰∏ã
			g[u][i] = &#39;Q&#39;;
			col[i] = dg[u + i] = udg[n - u + i] = true;
			dfs(u + 1);
			col[i] = dg[u + i] = udg[n - u + i] = false;
			g[u][i] = &#39;.&#39;;
		&amp;#125;
	&amp;#125;
&amp;#125;
int main()&amp;#123;
	cin &amp;gt;&amp;gt; n;
	for(int i = 0; i &amp;lt; n; i++)
		for(int j = 0; j &amp;lt; n; j++)
			g[i][j] = &#39;.&#39;;
	dfs(0);
	return 0;
&amp;#125;
