&lt;h3 id=&#34;‰∏∫‰ªÄ‰πàÊ±ÇÊà™Ë∑ùÂ∞±ÂèØ‰ª•Âà§Êñ≠‰∏§‰∏™ÁÇπÂú®‰∏ÄÊù°ÂØπËßíÁ∫øÊàñËÄÖÂèçÂØπËßíÁ∫øÂë¢&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#‰∏∫‰ªÄ‰πàÊ±ÇÊà™Ë∑ùÂ∞±ÂèØ‰ª•Âà§Êñ≠‰∏§‰∏™ÁÇπÂú®‰∏ÄÊù°ÂØπËßíÁ∫øÊàñËÄÖÂèçÂØπËßíÁ∫øÂë¢&#34;&gt;#&lt;/a&gt; ‰∏∫‰ªÄ‰πàÊ±ÇÊà™Ë∑ùÂ∞±ÂèØ‰ª•Âà§Êñ≠‰∏§‰∏™ÁÇπÂú®‰∏ÄÊù°ÂØπËßíÁ∫øÊàñËÄÖÂèçÂØπËßíÁ∫øÂë¢Ôºü&lt;/h3&gt;
&lt;pre&gt;&lt;code&gt;Âõ†‰∏∫bÂèØ‰ª•ÂîØ‰∏ÄÁ°ÆÂÆö‰∏ÄÊù°Áõ¥Á∫øÔºåÂú®Âêå‰∏ÄÊù°Áõ¥Á∫ø‰∏äÈù¢bÈÉΩÊòØÁõ∏ÂêåÁöÑÔºåÊâÄ‰ª•‰ª•b‰∏∫‰∏ãÊ†áÊòØÂèØ‰ª•ÁπÅÊÆñÂØπËßíÁ∫øÂíåÂèçÂØπËßíÁ∫ø‰∏äÈù¢ÊîæÁöáÂêéÁöÑ
&lt;/code&gt;&lt;/pre&gt;
&lt;pre&gt;&lt;code class=&#34;language-c++&#34;&gt;#include &amp;lt;iostream&amp;gt;

using namespace std;

const int N = 20;
int n;
char g[N][N];
bool row[N], col[N], dg[N], udg[N];

void dfs(int x, int y, int s)&amp;#123;
	if(y == n)&amp;#123;
		y = 0;
		x ++;
	&amp;#125;
	if(x == n)&amp;#123;
		if(s == n)&amp;#123;
			for(int i = 0; i &amp;lt; n; i++) puts(g[i]);
			puts(&amp;quot;&amp;quot;);
		&amp;#125;
		return;
	&amp;#125;
	//‰∏çÊîæÁöáÂêé
	dfs(x, y + 1, s);
	//ÊîæÁöáÂêé
	if(!row[x] &amp;amp;&amp;amp; !col[y] &amp;amp;&amp;amp; !dg[x + y] &amp;amp;&amp;amp;!udg[x - y + n])&amp;#123;
		g[x][y] = &#39;Q&#39;;
		row[x] = col[y] = dg[x + y] = udg[x - y + n] = true;
		dfs(x, y + 1, s + 1);
		row[x] = col[y] = dg[x + y] = udg[x - y + n] = false;
		g[x][y] = &#39;.&#39;;
	&amp;#125;
&amp;#125;
int main()&amp;#123;
	cin &amp;gt;&amp;gt; n;
	for(int i = 0; i &amp;lt; n; i++)&amp;#123;
		for(int j = 0; j &amp;lt; n; j++)&amp;#123;
			g[i][j] = &#39;.&#39;;
		&amp;#125;
	&amp;#125;
	dfs(0, 0, 0);
	return 0;
&amp;#125;
