#include &amp;lt;iostream&amp;gt;

using namespace std;

const int N = 20;

int n;
char g[N][N];
bool row[N], col[N], dg[N], udg[N];

void dfs(int x, int y, int s)&amp;#123;
    if(y == n) y = 0, x++;

    if(x == n)&amp;#123;
        if(s == n)&amp;#123;
            for(int i = 0; i &amp;lt; n; i++) puts(g[i]);
            puts(&amp;quot;&amp;quot;);
        &amp;#125;

        return;
        //ËøôËæπreturn‰∏ãÈù¢ÈÇ£‰∏™dfs‰πü‰ºöreturnÂá∫ÂéªÂà∞dfs‰∏ãÈù¢ÁöÑifÂéªÁöÑ
    &amp;#125;

    //‰∏çÊîæÁöáÂêé
    dfs(x, y + 1, s);

    //ÊîæÁöáÂêé
    if(!row[x] &amp;amp;&amp;amp; !col[y] &amp;amp;&amp;amp; !dg[x + y] &amp;amp;&amp;amp; !udg[x - y + n])&amp;#123;
        g[x][y] = &#39;Q&#39;;
        row[x] = col[y] = dg[x + y] = udg[x - y + n] = true;
        dfs(x, y + 1, s + 1);
        row[x] = col[y] = dg[x + y] = udg[x - y + n] = false;
        g[x][y] = &#39;.&#39;;
    &amp;#125;
